import urwid

from mitmproxy.tools.console import common
from mitmproxy.tools.console import layoutwidget
import mitmproxy.tools.console.main # noqa


class FlowItem(urwid.WidgetWrap):

    def __init__(self, main, flow):
        self.main, self.flow = main, flow
        w = self.get_text()
        urwid.WidgetWrap.__init__(self, w)

    def get_text(self):
        cols, _ = self.main.ui.get_cols_rows()
        return common.format_flow(
            self.flow,
            self.flow is self.main.view.focus.flow,
            hostheader=self.main.options.showhost,
            max_url_len=cols,
        )

    def selectable(self):
        return True

    def mouse_event(self, size, event, button, col, row, focus):
        if event == "mouse press" and button == 1:
            if self.flow.request:
                self.main.commands.execute("console.view.flow @focus")
                return True

    def keypress(self, size, key):
        return key


class FlowListWalker(urwid.ListWalker):

    def __init__(self, main):
        self.main = main

    def positions(self, reverse=False):
        # The stub implementation of positions can go once this issue is resolved:
        # https://github.com/urwid/urwid/issues/294
        ret = range(len(self.main.view))
        if reverse:
            return reversed(ret)
        return ret

    def view_changed(self):
        self._modified()

    def get_focus(self):
        if not self.main.view.focus.flow:
            return None, 0
        f = FlowItem(self.main, self.main.view.focus.flow)
        return f, self.main.view.focus.index

    def set_focus(self, index):
        if self.main.view.inbounds(index):
            self.main.view.focus.index = index

    def get_next(self, pos):
        pos = pos + 1
        if not self.main.view.inbounds(pos):
            return None, None
        f = FlowItem(self.main, self.main.view[pos])
        return f, pos

    def get_prev(self, pos):
        pos = pos - 1
        if not self.main.view.inbounds(pos):
            return None, None
        f = FlowItem(self.main, self.main.view[pos])
        return f, pos


class FlowListBox(urwid.ListBox, layoutwidget.LayoutWidget):
    title = "Flows"
    keyctx = "flowlist"

    def __init__(
        self, main: "mitmproxy.tools.console.main.ConsoleMain"
    ) -> None:
        self.main: "mitmproxy.tools.console.main.ConsoleMain" = main
        super().__init__(FlowListWalker(main))

    def keypress(self, size, key):
        if key == "m_start":
            self.main.commands.execute("view.go 0")
        elif key == "m_end":
            self.main.commands.execute("view.go -1")
        elif key == "m_select":
            self.main.commands.execute("console.view.flow @focus")
        return urwid.ListBox.keypress(self, size, key)

    def view_changed(self):
        self.body.view_changed()
